class DistrictRepository extends \Doctrine\ORM\EntityRepository
{
  /**
    * Selection du distrct concernÃ© par l'enregistrement du groupe
    * Pour GroupeType.php
    *
    * @author: Delrodie AMOIKON
    * @version v1.0 16/05/2017 10:31
