    public function getDistrictNom($district)
    {
       //die($user);
        $em = $this->getEntityManager();

        $qb = $this->createQueryBuilder('d')
                   ->where('d.id = :district')
                   ->setParameter('district', $district);
         return $qb;
    }
}
